const { App } = require("@slack/bolt");

const app = new App({
  logLevel: "debug", // これはログレベルの調整なので削除しても OK です
  socketMode: true,
  token: process.env.SLACK_BOT_TOKEN,
  appToken: process.env.SLACK_APP_TOKEN,
});

// グローバルショートカット
app.shortcut("socket-mode-shortcut", async ({ ack, body, client }) => {
  await ack();
  await client.views.open({
    trigger_id: body.trigger_id,
    view: {
      type: "modal",
      callback_id: "modal-id",
      title: {
        type: "plain_text",
        text: "タスク登録",
      },
      submit: {
        type: "plain_text",
        text: "送信",
      },
      close: {
        type: "plain_text",
        text: "キャンセル",
      },
      blocks: [
        {
          type: "input",
          block_id: "input-task",
          element: {
            type: "plain_text_input",
            action_id: "input",
            multiline: true,
            placeholder: {
              type: "plain_text",
              text: "タスクの詳細・期限などを書いてください",
            },
          },
          label: {
            type: "plain_text",
            text: "タスク",
          },
        },
      ],
    },
  });
});

app.view("modal-id", async ({ ack, view, logger }) => {
  logger.info(`Submitted data: ${view.state.values}`);
  await ack();
});

// イベント API
app.message("ここで働かせてください！", async ({ message, say }) => {
  await say(
    `<@${message.user}>なんであたしがおまえを雇わなきゃならないんだい！？見るからにグズで！甘ったれで！泣き虫で！頭の悪い小娘に、仕事なんかあるもんかね！お断りだね。これ以上穀潰しを増やしてどうしようっていうんだい！それとも……一番つらーーいきつーーい仕事を死ぬまでやらせてやろうかぁ……？`
  );
});

app.message("ここで働きたいんです！", async ({ message, say }) => {
  await say(
    `<@${message.user}>わかったから静かにしておくれ！おおぉお～よ～しよし～……`
  );
  await say(
    `<@${message.user}>契約書だよ。そこに名前を書きな。働かせてやる。その代わり嫌だとか、帰りたいとか言ったらすぐ子豚にしてやるからね。`
  );
});

(async () => {
  await app.start();
  console.log("⚡️ Bolt app started");
})();
